import './rightbar.css'
import {Users} from "../../dummyData";
import Onlinefriends from '../onlinefriends/onlinefriends';
import { useEffect } from 'react';

function Rightbar({user}) {
    const public_folder=process.env.REACT_APP_PUBLIC_FOLDER
    const [friends,setFriends]=useState()

    useEffect(()=>{
        const getFriends=async()=>{
            try{
                const friendList=await axios.get("/users/friends/"+user._id)
            }catch(err){
              console.log(err)  
            }
        }
    },[])

    const HomeRightbar=()=>{
        return (
            <>
              <div className="birthdayContainer">
                    <img src="assets/gift.png" alt="bday pic" className="birthdayImg" />
                    <span className="birthdayText">
                        <b>Pola Foster</b> and <b>3 other friends</b> have a birthday today!
                    </span>
                </div>
                <img src="/assets/ad.png" alt="advertisement" className="rightbarAd"/>
                <h4 className="rightbarTitle">Online Friends</h4>
                <ul className="rightbarFriendList">
                    {Users.map(user=>(<Onlinefriends key={user.id} u={user}/>))}
                </ul>
            </>
        )

    }

    const ProfileRightbar=()=>{
        return(
            <>
            <h4 className="rightbarTitle">User information</h4>
            <div className="rightbarInfo">
                <div className="rightbarInfoItem">
                    <span className="rightbarInfoKey">City:</span>
                    <span className="rightbarInfoValue">{user.city}</span>
                </div>
                <div className="rightbarInfoItem">
                    <span className="rightbarInfoKey">From:</span>
                    <span className="rightbarInfoValue">{user.from}</span>
                </div>
                <div className="rightbarInfoItem">
                    <span className="rightbarInfoKey">Relationship:</span>
                    <span className="rightbarInfoValue">{user.relationship===1?"Single":user.relationship===2?"Married":"-"}</span>
                </div>
            </div>
            <h4 className="rightbarTitle">User friends</h4>
            <div className="rightbarFollowings">
                <div className="rightbarFollowing">
                   <img src={`${public_folder}person/1.jpeg`} alt="" className="rightbarFollowingImg" />
                   <span className="rightbarFollowingNme">Jon Carter</span>
                </div>
            </div>

            </>
        )
    }

    return (
        <div className="rightbar">
            <div className="rightbarWrapper">
              {user?<ProfileRightbar/>:<HomeRightbar/>}
            </div>
        </div>
        
    )
}

export default Rightbar
