import {createContext,useReducer,useEffect} from "react";
import AuthReducer from './authReducer'

const INITIAL_STATE={
    user:JSON.parse(localStorage.getItem("user")) || null,
    isFetching:false,
    error: false
}

export const AuthContext=createContext(INITIAL_STATE)
